#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    long long k, n;
    scanf("%lld %lld", &k, &n);

    long long total_sequences = 0;
    for (long long i = 1; i <= n; i++) {
        total_sequences += pow(k, i);
    }

    long long target_index = (long long)ceil((double)total_sequences / 2);
    long long current_index = 0;

    long long *result = (long long *)malloc((n + 1) * sizeof(long long));
    long long result_length = 0;

    for (long long len = 1; len <= n; len++) {
        for (long long i = 0; i < pow(k, len); i++) {
            long long temp = i;
            long long num = len;
            long long *sequence = (long long *)malloc(len * sizeof(long long));
            while (num > 0) {
                sequence[num - 1] = (temp % k) + 1;
                temp /= k;
                num--;
            }
            current_index++;
            if (current_index == target_index) {
                result_length = len;
                for (long long j = 0; j < len; j++) {
                    result[j] = sequence[j];
                }
                free(sequence);
                break;
            }
            free(sequence);
        }
        if (current_index == target_index) break;
    }


    for (long long i = 0; i < result_length; i++) {
        printf("%lld", result[i]);
        if (i < result_length - 1) {
            printf(" ");
        }
    }
    printf("\n");
    free(result);
    return 0;
}
