#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int a[n][m];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            scanf("%d", &a[i][j]);
        }
    }
    int min_max = n;
    for (int i = 1; i < (1 << m); i++) {
        int count[m + 1];
        for (int j = 0; j <= m; j++) {
            count[j] = 0;
        }
        for (int j = 0; j < n; j++) {
            int fav = -1;
            for (int k = 0; k < m; k++) {
                if ((i >> k) & 1) {
                    if (fav == -1) {
                        fav = a[j][k];
                    } else {
                        int cur = a[j][k];
                        int idx1 = -1, idx2 = -1;
                        for(int l=0; l<m; ++l){
                            if(a[j][l] == fav) idx1 = l;
                            if(a[j][l] == cur) idx2 = l;
                        }
                        if(idx1 < idx2) fav = cur;
                    }
                }
            }
            count[fav]++;
        }
        int max_count = 0;
        for (int j = 1; j <= m; j++) {
            if (count[j] > max_count) {
                max_count = count[j];
            }
        }
        if (max_count < min_max) {
            min_max = max_count;
        }
    }
    printf("%d\n", min_max);
    return 0;
}
