#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> a(n, vector<int>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> a[i][j];
        }
    }

    int min_max_participants = n;
    for (int i = 1; i < (1 << m); ++i) {
        vector<int> selected_sports;
        for (int j = 0; j < m; ++j) {
            if ((i >> j) & 1) {
                selected_sports.push_back(j + 1);
            }
        }

        vector<int> participants(m + 1, 0);
        for (int j = 0; j < n; ++j) {
            int best_sport = -1;
            int min_rank = -1;
            for (int k = 0; k < m; ++k) {
                bool selected = false;
                for (int s : selected_sports) {
                    if (a[j][k] == s) {
                        selected = true;
                        break;
                    }
                }
                if (selected) {
                    int rank = 0;
                    for (int l = 0; l < m; ++l) {
                        if (a[j][l] == a[j][k]) {
                            break;
                        }
                        rank++;
                    }
                    if (best_sport == -1 || rank < min_rank) {
                        best_sport = a[j][k];
                        min_rank = rank;
                    }
                }
            }
            if (best_sport != -1) {
                participants[best_sport]++;
            }
        }

        int max_participants = 0;
        for (int j = 1; j <= m; ++j) {
            max_participants = max(max_participants, participants[j]);
        }
        min_max_participants = min(min_max_participants, max_participants);
    }

    cout << min_max_participants << endl;

    return 0;
}
