import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[][] a = new int[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                a[i][j] = sc.nextInt();
            }
        }
        int ans = n;
        for (int i = 0; i < (1 << m); i++) {
            List<Integer> sports = new ArrayList<>();
            for (int j = 0; j < m; j++) {
                if ((i >> j) % 2 == 1) {
                    sports.add(j + 1);
                }
            }
            if (sports.isEmpty()) continue;
            Map<Integer, Integer> count = new HashMap<>();
            for (int j = 0; j < n; j++) {
                int fav = -1;
                for (int k = 0; k < m; k++) {
                    if (sports.contains(a[j][k])) {
                        fav = a[j][k];
                        break;
                    }
                }
                count.put(fav, count.getOrDefault(fav, 0) + 1);
            }
            int max = 0;
            for (int c : count.values()) {
                max = Math.max(max, c);
            }
            ans = Math.min(ans, max);
        }
        System.out.println(ans);
    }
}
