def solve():
    n, m = map(int, input().split())
    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    ans = float('inf')
    for i in range(1, 1 << m):
        selected_sports = []
        for j in range(m):
            if (i >> j) & 1:
                selected_sports.append(j + 1)

        counts = {}
        for person_pref in a:
            best_sport = -1
            min_index = float('inf')
            for k in range(m):
                if person_pref[k] in selected_sports:
                    if k < min_index:
                        min_index = k
                        best_sport = person_pref[k]
            if best_sport != -1:
                counts[best_sport] = counts.get(best_sport, 0) + 1

        if counts:
            ans = min(ans, max(counts.values()))

    print(ans)

