#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Person {
    long long a, b, c;
};

int main() {
    long long x, y, z;
    cin >> x >> y >> z;

    long long n = x + y + z;
    vector<Person> people(n);
    for (int i = 0; i < n; ++i) {
        cin >> people[i].a >> people[i].b >> people[i].c;
    }

    long long max_coins = 0;
    vector<tuple<long long, long long, long long>> permutations;
    vector<int> p(n);
    for (int i = 0; i < n; ++i) p[i] = i;

    do {
        long long current_coins = 0;
        long long gold_count = 0;
        long long silver_count = 0;
        long long bronze_count = 0;
        vector<long long> gold_choices;
        vector<long long> silver_choices;
        vector<long long> bronze_choices;

        for (int i = 0; i < n; ++i) {
            if (gold_count < x) gold_choices.push_back(people[p[i]].a);
            if (silver_count < y) silver_choices.push_back(people[p[i]].b);
            if (bronze_count < z) bronze_choices.push_back(people[p[i]].c);
        }

        sort(gold_choices.begin(), gold_choices.end(), greater<long long>());
        sort(silver_choices.begin(), silver_choices.end(), greater<long long>());
        sort(bronze_choices.begin(), bronze_choices.end(), greater<long long>());

        for (int i = 0; i < x; ++i) current_coins += gold_choices[i];
        for (int i = 0; i < y; ++i) current_coins += silver_choices[i];
        for (int i = 0; i < z; ++i) current_coins += bronze_choices[i];

        max_coins = max(max_coins, current_coins);
    } while (next_permutation(p.begin(), p.end()));

    cout << max_coins << endl;

    return 0;
}
