import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long X = sc.nextLong();
        long Y = sc.nextLong();
        long Z = sc.nextLong();
        long n = X + Y + Z;
        long[][] arr = new long[(int) n][3];
        for (int i = 0; i < n; i++) {
            arr[i][0] = sc.nextLong();
            arr[i][1] = sc.nextLong();
            arr[i][2] = sc.nextLong();
        }
        long ans = 0;
        for (int i = 0; i < (1 << (int) n); i++) {
            long cur = 0;
            long x = 0;
            long y = 0;
            long z = 0;
            long[] gold = new long[(int)n];
            long[] silver = new long[(int)n];
            long[] bronze = new long[(int)n];
            for(int j=0; j<n; ++j){
                gold[j] = arr[j][0];
                silver[j] = arr[j][1];
                bronze[j] = arr[j][2];
            }
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 0) {
                    if (x < X) {
                        cur += gold[j];
                        x++;
                    } else if (y < Y) {
                        cur += silver[j];
                        y++;
                    } else if (z < Z) {
                        cur += bronze[j];
                        z++;
                    }
                } else if ((i >> j) % 2 == 1) {
                    if (x < X) {
                        cur += gold[j];
                        x++;
                    } else if (y < Y) {
                        cur += silver[j];
                        y++;
                    } else if (z < Z) {
                        cur += bronze[j];
                        z++;
                    }
                }
            }
            if(x == X && y ==Y && z == Z) ans = Math.max(ans, cur);
        }
        System.out.println(ans);
        sc.close();
    }
}
