#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

int main() {
  long long k, n;
  cin >> k >> n;

  long long total_sequences = 0;
  for (long long i = 1; i <= n; ++i) {
    total_sequences += pow(k, i);
  }

  long long target_index = (long long)ceil((double)total_sequences / 2.0);

  vector<long long> result;
  long long current_index = target_index;

  for (long long i = n; i >= 1; --i) {
    long long power = pow(k, i -1);
    long long num = (current_index -1) / power;
    result.push_back(num + 1);
    current_index -= num * power;
  }

  for (long long i = 0; i < result.size(); ++i) {
    cout << result[i] << (i == result.size() - 1 ? "" : " ");
  }
  cout << endl;

  return 0;
}
