#include <stdio.h>

#define MOD 1000000007

int main() {
    long long X1, X2, X3, X4, X5, X6;
    long long Y1, Y2, Y3, Y4, Y5, Y6;
    scanf("%lld %lld %lld %lld %lld %lld", &X1, &X2, &X3, &X4, &X5, &X6);
    scanf("%lld %lld %lld %lld %lld %lld", &Y1, &Y2, &Y3, &Y4, &Y5, &Y6);

    long long ans = 0;
    for (long long start_x = X1; start_x <= X2; start_x++) {
        for (long long start_y = Y1; start_y <= Y2; start_y++) {
            for (long long lunch_x = X3; lunch_x <= X4; lunch_x++) {
                for (long long lunch_y = Y3; lunch_y <= Y4; lunch_y++) {
                    for (long long end_x = X5; end_x <= X6; end_x++) {
                        for (long long end_y = Y5; end_y <= Y6; end_y++) {
                            long long dist_start_lunch = abs(start_x - lunch_x) + abs(start_y - lunch_y);
                            long long dist_lunch_end = abs(lunch_x - end_x) + abs(lunch_y - end_y);
                            long long dist_start_end = abs(start_x - end_x) + abs(start_y - end_y);

                            if (dist_start_lunch + dist_lunch_end == dist_start_end) {
                                ans = (ans + 1) % MOD;
                            }
                        }
                    }
                }
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
