#include <iostream>

using namespace std;

int main() {
  long long x1, x2, x3, x4, x5, x6;
  long long y1, y2, y3, y4, y5, y6;

  cin >> x1 >> x2 >> x3 >> x4 >> x5 >> x6;
  cin >> y1 >> y2 >> y3 >> y4 >> y5 >> y6;

  long long count = 0;
  long long mod = 1e9 + 7;

  for (long long p = x1; p <= x2; ++p) {
    for (long long q = y1; q <= y2; ++q) {
      for (long long s = x3; s <= x4; ++s) {
        for (long long t = y3; t <= y4; ++t) {
          for (long long u = x5; u <= x6; ++u) {
            for (long long v = y5; v <= y6; ++v) {
              if (abs(p - s) + abs(q - t) + abs(s - u) + abs(t - v) == abs(p - u) + abs(q - v))
                count = (count + 1) % mod;
            }
          }
        }
      }
    }
  }

  cout << count << endl;
  return 0;
}
