a = list(map(int, input().split()))
b = list(map(int, input().split()))

graph_a = [[] for _ in range(n)]
graph_b = [[] for _ in range(n)]
roots_a = []
roots_b = []

for i in range(n):
    if a[i] == -1:
        roots_a.append(i)
    else:
        graph_a[a[i] - 1].append(i)
    if b[i] == -1:
        roots_b.append(i)
    else:
        graph_b[b[i] - 1].append(i)

x = [0] * n
possible = True

def dfs_a(u, val):
    s = val
    for v in graph_a[u]:
        s += dfs_a(v, 1 if s == 0 else -1)
    return s

def dfs_b(u, val):
    s = val
    for v in graph_b[u]:
        s += dfs_b(v, 1 if s == 0 else -1)
    return s

if len(roots_a) == 1 and len(roots_b) ==1:
    x[roots_a[0]] = 1
    dfs_a(roots_a[0],1)
    if abs(dfs_b(roots_b[0],1)) != 1:
        possible = False

if possible:
    print("POSSIBLE")
    for i in range(n):

        print(1 if i == roots_a[0] else 0,end=" ")

else:
    print("IMPOSSIBLE")
