#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, Z, W;
    scanf("%d %d %d", &N, &Z, &W);
    int a[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
    }
    long long max_diff = 0;
    for (int i = 1; i < (1 << N); i++) {
        long long current_x = Z;
        long long current_y = W;
        int x_turn = 1;
        int cards_left = N;
        int current_index = 0;
        while (cards_left > 0) {
            int num_cards = 0;
            int next_index = current_index;
            int best_card = -1;
            for (int j = 0; j < cards_left; j++) {
                if ((i >> (current_index + j)) & 1) {
                    num_cards++;
                    best_card = a[current_index + j];
                } else {
                    break;
                }
            }
            if (x_turn) {
                current_x = best_card;
            } else {
                current_y = best_card;
            }
            current_index += num_cards;
            cards_left -= num_cards;
            x_turn = 1 - x_turn;

        }
        max_diff = (max_diff < abs(current_x - current_y)) ? abs(current_x - current_y) : max_diff;
    }
    printf("%lld\n", max_diff);
    return 0;
}
