#include <stdio.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int l[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &l[i]);
    }
    int sum = 0;
    for (int i = 0; i < K; i++) {
        int max = 0;
        int index = -1;
        for (int j = 0; j < N; j++) {
            if (l[j] > max) {
                max = l[j];
                index = j;
            }
        }
        sum += max;
        l[index] = 0;
    }
    printf("%d\n", sum);
    return 0;
}
