#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>

int main() {
    int N, K;
    std::cin >> N >> K;
    std::vector<long long> l(N);
    for (int i = 0; i < N; ++i) {
        std::cin >> l[i];
    }
    std::sort(l.begin(), l.end(), std::greater<long long>());
    long long sum = 0;
    for (int i = 0; i < K; ++i) {
        sum += l[i];
    }
    std::cout << sum << std::endl;
    return 0;
}
