#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    long long sum = 0;
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
        sum += a[i];
    }
    long long min_diff = LLONG_MAX;
    long long current_sum = 0;
    for (int i = 0; i < n - 1; i++) {
        current_sum += a[i];
        long long diff = abs(current_sum - (sum - current_sum));
        if (diff < min_diff) {
            min_diff = diff;
        }
    }
    printf("%lld\n", min_diff);
    return 0;
}
