#include <iostream>
#include <vector>
#include <climits>
#include <cmath>

using namespace std;

int main() {
    long long n;
    cin >> n;

    vector<long long> a(n);
    long long sum = 0;
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        sum += a[i];
    }

    long long min_diff = LLONG_MAX;
    long long current_sum = 0;
    for (int i = 0; i < n - 1; ++i) {
        current_sum += a[i];
        min_diff = min(min_diff, abs(current_sum - (sum - current_sum)));
    }

    cout << min_diff << endl;
    return 0;
}
