#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int *a = (int *)malloc(n * sizeof(int));
    int *b = (int *)malloc(n * sizeof(int));
    int **adj = (int **)malloc(n * sizeof(int *));
    for (int i = 0; i < n; i++) {
        adj[i] = (int *)malloc(n * sizeof(int));
        for (int j = 0; j < n; j++)
            adj[i][j] = 0;
    }
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &a[i], &b[i]);
        a[i]--;
        b[i]--;
        adj[a[i]][b[i]] = adj[b[i]][a[i]] = 1;
    }
    int black_count = 1;
    int white_count = 0;
    int *color = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++)
        color[i] = 0;
    color[0] = 1;
    color[n - 1] = 2;
    white_count = 1;
    int turn = 1;
    while (1) {
        int moved = 0;
        if (turn == 1) {
            for (int i = 0; i < n; i++) {
                if (color[i] == 0) {
                    for (int j = 0; j < n; j++) {
                        if (adj[i][j] == 1 && color[j] == 1) {
                            color[i] = 1;
                            black_count++;
                            moved = 1;
                            break;
                        }
                    }
                }
                if (moved)
                    break;
            }
        } else {
            for (int i = 0; i < n; i++) {
                if (color[i] == 0) {
                    for (int j = 0; j < n; j++) {
                        if (adj[i][j] == 1 && color[j] == 2) {
                            color[i] = 2;
                            white_count++;
                            moved = 1;
                            break;
                        }
                    }
                }
                if (moved)
                    break;
            }
        }
        if (!moved)
            break;
        turn = 3 - turn;
    }
    if (black_count > white_count)
        printf("Fennec\n");
    else
        printf("Snuke\n");
    return 0;
}
