#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  vector<vector<int>> adj(n);
  for (const auto& edge : edges) {
    adj[get<0>(edge)].push_back(get<1>(edge));
    adj[get<1>(edge)].push_back(get<0>(edge));
  }

  vector<int> color(n, 0);
  color[0] = 1;
  color[n - 1] = 2;

  int black_count = 1;
  int white_count = 1;

  bool fennec_turn = true;
  while (true) {
    bool moved = false;
    if (fennec_turn) {
      for (int i = 0; i < n; ++i) {
        if (color[i] == 0) {
          bool adjacent_to_black = false;
          for (int neighbor : adj[i]) {
            if (color[neighbor] == 1) {
              adjacent_to_black = true;
              break;
            }
          }
          if (adjacent_to_black) {
            color[i] = 1;
            black_count++;
            moved = true;
            break;
          }
        }
      }
    } else {
      for (int i = 0; i < n; ++i) {
        if (color[i] == 0) {
          bool adjacent_to_white = false;
          for (int neighbor : adj[i]) {
            if (color[neighbor] == 2) {
              adjacent_to_white = true;
              break;
            }
          }
          if (adjacent_to_white) {
            color[i] = 2;
            white_count++;
            moved = true;
            break;
          }
        }
      }
    }
    if (!moved) break;
    fennec_turn = !fennec_turn;
  }

  if (black_count > white_count) {
    cout << "Fennec" << endl;
  } else {
    cout << "Snuke" << endl;
  }
  return 0;
}
