import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int[] color = new int[n];
        color[0] = 1;
        color[n - 1] = 2;
        int blackCount = 1;
        int whiteCount = 1;
        int turn = 1;
        while (true) {
            boolean moved = false;
            if (turn == 1) {
                for (int i = 0; i < n; i++) {
                    if (color[i] == 0) {
                        boolean adjacentToBlack = false;
                        for (int neighbor : adj.get(i)) {
                            if (color[neighbor] == 1) {
                                adjacentToBlack = true;
                                break;
                            }
                        }
                        if (adjacentToBlack) {
                            color[i] = 1;
                            blackCount++;
                            moved = true;
                            break;
                        }
                    }
                }
            } else {
                for (int i = 0; i < n; i++) {
                    if (color[i] == 0) {
                        boolean adjacentToWhite = false;
                        for (int neighbor : adj.get(i)) {
                            if (color[neighbor] == 2) {
                                adjacentToWhite = true;
                                break;
                            }
                        }
                        if (adjacentToWhite) {
                            color[i] = 2;
                            whiteCount++;
                            moved = true;
                            break;
                        }
                    }
                }
            }
            if (!moved) {
                if (turn == 1) {
                    System.out.println("Snuke");
                } else {
                    System.out.println("Fennec");
                }
                break;
            }
            turn = 3 - turn;
        }
    }
}
