#include <iostream>
#include <vector>
#include <cmath>
#include <limits>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_diff = numeric_limits<long long>::max();
  for (int i = 1; i < n; ++i) {
    long long sum_snuke = 0;
    for (int j = 0; j < i; ++j) {
      sum_snuke += a[j];
    }
    long long sum_raccoon = 0;
    for (int j = i; j < n; ++j) {
      sum_raccoon += a[j];
    }
    min_diff = min(min_diff, abs(sum_snuke - sum_raccoon));
  }

  cout << min_diff << endl;
  return 0;
}
