import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        long sum = 0;
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
            sum += a[i];
        }
        long minDiff = Long.MAX_VALUE;
        long currentSum = 0;
        for (int i = 0; i < n - 1; i++) {
            currentSum += a[i];
            long diff = Math.abs(2 * currentSum - sum);
            minDiff = Math.min(minDiff, diff);
        }
        System.out.println(minDiff);
    }
}
