#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n;
int a[MAXN], b[MAXN];
int color[MAXN];
int adj[MAXN][2];
int adj_count[MAXN];

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &a[i], &b[i]);
        a[i]--;
        b[i]--;
        adj[a[i]][adj_count[a[i]]++] = b[i];
        adj[b[i]][adj_count[b[i]]++] = a[i];
    }
    color[0] = 1;
    color[n - 1] = 2;
    int black_count = 1;
    int white_count = 1;
    int turn = 1;
    while (1) {
        int found = 0;
        if (turn == 1) {
            for (int i = 0; i < n; i++) {
                if (color[i] == 0) {
                    for (int j = 0; j < adj_count[i]; j++) {
                        if (color[adj[i][j]] == 1) {
                            color[i] = 1;
                            black_count++;
                            found = 1;
                            break;
                        }
                    }
                }
                if (found) break;
            }
        } else {
            for (int i = 0; i < n; i++) {
                if (color[i] == 0) {
                    for (int j = 0; j < adj_count[i]; j++) {
                        if (color[adj[i][j]] == 2) {
                            color[i] = 2;
                            white_count++;
                            found = 1;
                            break;
                        }
                    }
                }
                if (found) break;
            }
        }
        if (!found) break;
        turn = 3 - turn;
    }
    if (black_count > white_count) printf("Fennec\n");
    else printf("Snuke\n");
    return 0;
}
