import collections

def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    graph = collections.defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    black = {0}
    white = {n - 1}
    uncolored = set(range(n)) - black - white

    turn = 0
    while True:
        if turn % 2 == 0:
            found = False
            for u in list(uncolored):
                for v in graph[u]:
                    if v in black:
                        black.add(u)
                        uncolored.remove(u)
                        found = True
                        break
                if found:
                    break
            if not found:
                print("Snuke")
                return
        else:
            found = False
            for u in list(uncolored):
                for v in graph[u]:
                    if v in white:
                        white.add(u)
                        uncolored.remove(u)
                        found = True
                        break
                if found:
                    break
            if not found:
                print("Fennec")
                return
        turn += 1

solve()
