#include <stdio.h>
#include <string.h>

int main() {
    long long left = 1, right = 1e9;
    long long ans = 0;
    while (left <= right) {
        long long mid = left + (right - left) / 2;
        printf("? %lld\n", mid);
        fflush(stdout);
        char response[2];
        scanf("%s", response);
        if (response[0] == 'Y') {
            ans = mid;
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }
    long long l = 0, r = 1e18;
    while(l <= r){
        long long mid = l + (r - l) / 2;
        char s_mid[20];
        sprintf(s_mid,"%lld",mid);
        char s_ans[20];
        sprintf(s_ans,"%lld",ans);
        printf("? %lld\n",mid);
        fflush(stdout);
        char response[2];
        scanf("%s", response);
        if(response[0] == 'Y'){
            if(strcmp(s_mid, s_ans) > 0) l = mid + 1;
            else r = mid -1;
        } else {
            if(strcmp(s_mid, s_ans) > 0) r = mid -1;
            else l = mid + 1;
        }
    }
    printf("! %lld\n",ans);
    fflush(stdout);
    return 0;
}
