#include <iostream>
#include <string>

using namespace std;

int main() {
  long long left = 1, right = 1e9;
  while (left <= right) {
    long long mid = left + (right - left) / 2;
    cout << "? " << mid << endl;
    fflush(stdout);
    char ans;
    cin >> ans;
    if (ans == 'Y') {
      long long cur = mid;
      long long next = mid + 1;
      string cur_str = to_string(cur);
      while (true) {
        string next_str = to_string(next);
        cout << "? " << next << endl;
        fflush(stdout);
        cin >> ans;
        if (ans == 'N') {
          cout << "! " << cur << endl;
          return 0;
        }
        cur = next;
        cur_str = next_str;
        next++;
      }
    } else {
      right = mid - 1;
    }
  }
  return 0;
}
