#include <stdio.h>

int main() {
    int N, P;
    scanf("%d %d", &N, &P);
    int A[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
    }
    long long count = 0;
    for (int i = 0; i < (1 << N); i++) {
        int sum = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                sum += A[j];
            }
        }
        if (sum % 2 == P) {
            count++;
        }
    }
    printf("%lld\n", count);
    return 0;
}
