#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, p;
  cin >> n >> p;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  for (int i = 0; i < (1 << n); ++i) {
    long long sum = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        sum += a[j];
      }
    }
    if (sum % 2 == p) {
      count++;
    }
  }

  cout << count << endl;
  return 0;
}
