using namespace std;

int main() {
  long long n, a, b, c, d;
  cin >> n >> a >> b >> c >> d;

  long long min_val = a, max_val = a;
  for (long long i = 1; i < n; ++i) {
    long long next_min = max(min_val - d, max_val - c);
    long long next_max = min(min_val + d, max_val + c);
    if (next_min > next_max) {
      cout << "NO" << endl;
      return 0;
    }
    min_val = next_min;
    max_val = next_max;
  }

  if (min_val <= b && b <= max_val) {
    cout << "YES" << endl;
  } else {
    cout << "NO" << endl;
  }

  return 0;
}
