import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long c = sc.nextLong();
        long d = sc.nextLong();

        if (n == 3) {
            if (Math.abs(a - b) >= c && Math.abs(a - b) <= d) {
                System.out.println("YES");
            } else {
                System.out.println("NO");
            }
            return;
        }

        long min = a - (n - 2) * d;
        long max = a + (n - 2) * d;

        if (b >= min && b <= max) {
            System.out.println("YES");
        } else {
            System.out.println("NO");
        }
    }
}
