#include <iostream>
#include <vector>

using namespace std;

int solve() {
  int n, m;
  cin >> n >> m;

  vector<int> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  for (int i = 0; i < m; ++i) {
    int x, y;
    cin >> x >> y;
    a[x - 1] = y;

    vector<int> b = a;
    int modifications = 0;
    while (!b.empty()) {
      int k = b.size();
      vector<int> next_b;
      bool modified = false;
      for (int j = 0; j < b.size(); ++j) {
        if (b[j] != k) {
          next_b.push_back(b[j]);
        } else {
          if (!modified) {
            modified = true;
          }
        }
      }
      if (!modified && !next_b.empty()) {
          modifications++;

          int min_val = next_b[0];
          for(int val : next_b){
              min_val = min(min_val,val);
          }
          bool found = false;
          for(int j=0; j< next_b.size();++j){
              if(next_b[j] == min_val){
                  found = true;
                  break;
              }
          }
          if(!found){
            modifications++;
          }

      }
      b = next_b;
    }
    cout << modifications << endl;
  }
  return 0;
}

int main() {
  solve();
  return 0;
}
