import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        for (int i = 0; i < m; i++) {
            int x = sc.nextInt() - 1;
            int y = sc.nextInt();
            a[x] = y;
            int count = 0;
            int[] b = Arrays.copyOf(a, n);
            int k = n;
            while (k > 0) {
                int c = 0;
                for (int j = 0; j < k; j++) {
                    if (b[j] == k) {
                        c++;
                    }
                }
                if (c == 0) {
                    count++;
                    break;
                }
                int[] d = new int[k - c];
                int idx = 0;
                for (int j = 0; j < k; j++) {
                    if (b[j] != k) {
                        d[idx++] = b[j];
                    }
                }
                b = d;
                k -= c;
            }
            System.out.println(count);
        }
    }
}
