#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  if (n == 2) {
    cout << "Alice" << endl;
    return 0;
  }

  vector<tuple<int, int>> edges(n - 1);
  vector<vector<int>> adj(n);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
    adj[get<0>(edges[i])].push_back(get<1>(edges[i]));
    adj[get<1>(edges[i])].push_back(get<0>(edges[i]));
  }

  int leaves = 0;
  for (int i = 0; i < n; ++i) {
    if (adj[i].size() == 1 && i != 0) {
      leaves++;
    }
  }

  if (leaves % 2 == 0) {
    cout << "Bob" << endl;
  } else {
    cout << "Alice" << endl;
  }
  return 0;
}
