import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int x = sc.nextInt();
            int y = sc.nextInt();
            graph.get(x).add(y);
            graph.get(y).add(x);
        }
        int count = 0;
        for (int i = 2; i <= n; i++) {
            boolean connected = false;
            Queue<Integer> q = new LinkedList<>();
            q.add(1);
            Set<Integer> visited = new HashSet<>();
            visited.add(1);
            while (!q.isEmpty()) {
                int u = q.poll();
                if (u == i) {
                    connected = true;
                    break;
                }
                for (int v : graph.get(u)) {
                    if (!visited.contains(v)) {
                        visited.add(v);
                        q.add(v);
                    }
                }
            }
            if (!connected) count++;
        }
        if (count % 2 == 1) {
            System.out.println("Alice");
        } else {
            System.out.println("Bob");
        }
    }
}
