n, h = map(int, input().split())
pieces = []
for i in range(n):
    a, b, c, d = map(int, input().split())
    pieces.append((a, b, c, d))

in_degree = [0] * n
graph = [[] for _ in range(n)]
for i in range(n):
    for j in range(n):
        if i != j:
            if pieces[i][0] <= pieces[j][1] and pieces[i][0] + pieces[j][1] >= h and pieces[i][2] + pieces[j][3] == h - pieces[i][0] and pieces[i][2] >= 0 and pieces[j][3] >=0:
                in_degree[j] += 1
                graph[i].append(j)
            elif pieces[j][0] <= pieces[i][1] and pieces[j][0] + pieces[i][1] >= h and pieces[j][2] + pieces[i][3] == h - pieces[j][0] and pieces[j][2] >= 0 and pieces[i][3] >=0:
                in_degree[i] += 1
                graph[j].append(i)


queue = []
for i in range(n):
    if in_degree[i] == 0:
        queue.append(i)

count = 0
while queue:
    curr = queue.pop(0)
    count += 1
    for neighbor in graph[curr]:
        in_degree[neighbor] -= 1
        if in_degree[neighbor] == 0:
            queue.append(neighbor)

if count == n:
    print("YES")
else:
    print("NO")

