#include <stdio.h>

#define MOD 1000000007

int main() {
    int N, M, K;
    scanf("%d %d %d", &N, &M, &K);
    int conditions[K][3];
    for (int i = 0; i < K; i++) {
        scanf("%d %d %d", &conditions[i][0], &conditions[i][1], &conditions[i][2]);
        conditions[i][0]--; 
        conditions[i][1]--; 
    }

    long long count = 0;
    long long paths[M][N];
    for (int i = 0; i < M; i++) {
        for (int j = 0; j < N; j++) {
            paths[i][j] = 0;
        }
    }


    long long total_paths = 1;
    for (int i = 0; i < M; ++i) {
        total_paths = (total_paths * (1LL << (N-1))) % MOD;
    }

    for (long long i = 0; i < total_paths; ++i) {
        long long temp = i;
        int valid = 1;
        for (int j = 0; j < M; ++j) {
            paths[j][0] = 1;
            for (int k = 0; k < N - 1; ++k) {
                int move = temp % 2;
                paths[j][k+1] = paths[j][k] + move;
                temp /= 2;
            }
        }

        for (int j = 0; j < N; ++j) {
            for (int k = 1; k < M; ++k) {
                if (paths[k][j] < paths[k-1][j]) {
                    valid = 0;
                    break;
                }
            }
            if (!valid) break;
        }

        for (int j = 0; j < K; ++j) {
            if ((paths[conditions[j][0]][conditions[j][1] + 1] - paths[conditions[j][0]][conditions[j][1]]) != conditions[j][2]) {
                valid = 0;
                break;
            }
        }
        if (valid) count++;
    }

    printf("%lld\n", count % MOD);
    return 0;
}
