N, M, K = map(int, input().split())
MOD = 1000000007
conditions = {}
for _ in range(K):
    A, B, C = map(int, input().split())
    conditions[(A, B)] = C

count = 0
for i in range(2 ** (N - 1) * M):
    paths = []
    valid = True
    temp = i
    for j in range(M):
        path = [1]
        for k in range(N - 1):
            bit = temp % 2
            path.append(path[-1] + bit)
            temp //= 2
        paths.append(path)
    for j in range(N):
        for k in range(M - 1):
            if paths[k][j] > paths[k + 1][j]:
                valid = False
                break
        if not valid:
            break
    if valid:
        for (A, B), C in conditions.items():
            if paths[A - 1][B] + C != paths[A - 1][B + 1]:
                valid = False
                break
    if valid:
        count += 1

print(count % MOD)

