#include <stdio.h>
#include <string.h>

int main() {
    char s[201];
    scanf("%s", s);
    int n = strlen(s);
    int max_len = 0;
    for (int i = n / 2; i > 0; i--) {
        int len = 2 * i;
        char sub[201];
        strncpy(sub, s, len);
        sub[len] = '\0';
        char first[101];
        strncpy(first, sub, i);
        first[i] = '\0';
        char second[101];
        strncpy(second, sub + i, i);
        second[i] = '\0';
        if (strcmp(first, second) == 0) {
            max_len = len;
            break;
        }
    }
    printf("%d\n", max_len);
    return 0;
}
