import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int n = s.length();
        for (int i = n / 2; i > 0; i--) {
            String s1 = s.substring(0, i);
            String s2 = s.substring(i, 2 * i);
            if (s1.equals(s2)) {
                System.out.println(2 * i);
                break;
            }
        }
    }
}
