#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long b[n];
    int head = 0;
    int tail = 0;
    int reverse_flag = 0;
    for (int i = 0; i < n; i++) {
        if (reverse_flag == 0) {
            b[tail++] = a[i];
        } else {
            b[head++] = a[i];
        }
        reverse_flag = 1 - reverse_flag;
    }
    if (n % 2 == 0) {
        for (int i = n - 1; i >= 0; i--) {
            printf("%lld ", b[i]);
        }
    } else {
        for (int i = 0; i < n; i++) {
            printf("%lld ", b[i]);
        }
    }
    printf("\n");
    return 0;
}
