import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Long> a = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            a.add(sc.nextLong());
        }
        List<Long> b = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            b.add(a.get(i));
            Collections.reverse(b);
        }
        for (int i = 0; i < n; i++) {
            System.out.print(b.get(i) + (i == n - 1 ? "" : " "));
        }
        System.out.println();
    }
}
