#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int a[n + 1];
    for (int i = 0; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    long long dp[n + 1][n + 1];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }
    for (int i = 1; i <= n; i++) {
        dp[0][i] = 1;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = dp[i - 1][j];
            if (a[i - 1] == j) {
                dp[i][j] = (dp[i][j] + dp[i - 1][j - 1]) % MOD;
            }
        }
    }
    for (int i = 1; i <= n + 1; i++) {
        long long ans = 0;
        for (int j = 1; j <= n; j++) {
            ans = (ans + dp[n][j]) % MOD;
        }
        printf("%lld\n", ans);
    }
    return 0;
}
