#include <stdio.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int a[n + 1];
    for (int i = 0; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    long long dp[n + 1][n + 2];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n + 1; j++) {
            dp[i][j] = 0;
        }
    }
    for (int i = 1; i <= n; i++) {
        dp[0][i] = 1;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n + 1; j++) {
            dp[i][j] = dp[i - 1][j];
            if (a[i - 1] == j) {
                for (int k = 1; k <= j; k++) {
                    if (dp[i - 1][k] > 0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }
    }
    long long ans[n + 2];
    for (int i = 1; i <= n + 1; i++) {
        ans[i] = 0;
    }
    for (int i = 1; i <= n + 1; i++) {
        ans[i] = dp[n][i];
        printf("%lld\n", ans[i]);
    }
    return 0;
}
