#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long min_presses = -1;
    for (int x = 1; x <= m; x++) {
        long long presses = 0;
        for (int i = 0; i < n - 1; i++) {
            int diff = abs(a[i] - a[i + 1]);
            int press1 = diff;
            int press2 = m - diff;
            int press3 = 1 + (m - a[i] + x - 1) % m + (m - a[i+1] + x -1 ) %m;
            int press4 = 1 + (a[i] - x + m -1)%m + (a[i+1] - x + m - 1 ) %m;

            int press_x = (m - a[i] + x - 1) % m + (a[i + 1] - x + m - 1) % m + 1;
            int press_x2 = (a[i] - x + m - 1) % m + (m - a[i + 1] + x - 1) % m + 1;
            int cur_press = press_x < press_x2 ? press_x : press_x2;

            presses += cur_press < press1 ? cur_press: press1;
        }
        if (min_presses == -1 || presses < min_presses) {
            min_presses = presses;
        }
    }
    printf("%lld\n", min_presses);
    return 0;
}
