#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_presses = -1;

  for (long long x = 1; x <= m; ++x) {
    long long current_presses = 0;
    for (long long i = 0; i < n - 1; ++i) {
      long long diff = abs(a[i] - a[i + 1]);
      long long presses_forward = min(diff, m - diff);
      long long presses_fav = 1 + min(abs(a[i+1] - x), m - abs(a[i+1] - x));

      current_presses += min(presses_forward, presses_fav);

    }
    if (min_presses == -1 || current_presses < min_presses) {
      min_presses = current_presses;
    }
  }
  cout << min_presses << endl;
  return 0;
}
