import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long minPresses = Long.MAX_VALUE;
        for (int x = 1; x <= m; x++) {
            long presses = 0;
            for (int i = 0; i < n - 1; i++) {
                int diff = (a[i + 1] - a[i] + m) % m;
                int diff2 = (a[i] - a[i + 1] + m) % m;
                int press1 = Math.min(diff, diff2);
                int press2 = 1 + Math.min((x - a[i] + m) % m + (a[i + 1] - x + m) % m, (a[i] - x + m) % m + (x - a[i + 1] + m) % m);
                presses += Math.min(press1, press2);
            }
            minPresses = Math.min(minPresses, presses);
        }
        System.out.println(minPresses);
    }
}
