#include <stdio.h>
#include <string.h>

int main() {
    char S[200005];
    long long l, r;
    scanf("%s %lld %lld", S, &l, &r);
    long long len = strlen(S);
    long long ans[26] = {0};
    for (long long i = 0; i < len; i++) {
        ans[S[i] - 'a']++;
    }
    long long total = r - l + 1;
    for (long long i = 0; i < 26; i++) {
        printf("%lld ", ans[i] * (total / len) + (total % len >= l && total % len <= r ? (total % len < len / 2 ? ans[i] * (total%len) / (len / 2) : ans[i] * (total%len - len / 2) / (len / 2) + ans[i]*(len/2) / (len/2) ) : 0));
    }
    printf("\n");
    return 0;
}
