#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
    string s;
    long long l, r;
    cin >> s >> l >> r;

    long long n = s.length();
    string f_s = s + s;

    vector<long long> counts(26, 0);
    for (long long i = l - 1; i < r; ++i) {
        counts[f_s[i % n] - 'a']++;
    }

    for (int i = 0; i < 26; ++i) {
        cout << counts[i] << (i == 25 ? "" : " ");
    }
    cout << endl;

    return 0;
}
