import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        long l = sc.nextLong();
        long r = sc.nextLong();
        long n = s.length();
        String f_s = s + s;
        long[] count = new long[26];
        for (long i = l - 1; i < r; i++) {
            count[f_s.charAt((int) (i % n)) - 'a']++;
        }
        for (long i = 0; i < 26; i++) {
            System.out.print(count[i] + " ");
        }
        System.out.println();
    }
}
