#include <stdio.h>
#include <limits.h>

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    long long min_val = LLONG_MAX;
    for (int i = 0; i <= n; ++i) {
        long long val = (1LL << (n - i)) + (long long)i * k;
        if (val < min_val) {
            min_val = val;
        }
    }
    printf("%lld\n", min_val);
    return 0;
}
