#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    int current = 1;
    int count = 0;
    int visited[n + 1];
    for (int i = 1; i <= n; i++) {
        visited[i] = 0;
    }
    while (current != 2) {
        if (visited[current] == 1) {
            printf("-1\n");
            return 0;
        }
        visited[current] = 1;
        current = a[current];
        count++;
        if (count > n) {
            printf("-1\n");
            return 0;
        }
    }
    printf("%d\n", count);
    return 0;
}
